// Меню
window.addEventListener('load', () => {


  let navbar_toggler = document.querySelector('.navbar-toggler')
  navbar_toggler.addEventListener('click', show_menu)

  function show_menu (e) {
    let menu_nav = document.querySelector('#menu_nav')
    menu_nav.style.display = 'block'
  }

  let hide_menu = document.querySelector('#hide_menu')
  hide_menu.addEventListener('click', hide_menu_fn)
  function hide_menu_fn () {
    let menu_nav = document.querySelector('#menu_nav')
    menu_nav.style.display = 'none'
  }

// Таблица
  let wi = window.innerWidth
    || document.documentElement.clientWidth
    || document.body.clientWidth

  window.addEventListener('resize', () => {
    wi = window.innerWidth
      || document.documentElement.clientWidth
      || document.body.clientWidth
  })

  window.addEventListener('scroll', tab_show)
  function tab_show (e) {
    if(wi < 845) {
      let height_scroll = window.pageYOffset || document.documentElement.scrollTop
      if (height_scroll > 365) {
        let table_block = document.querySelector('#table_block')
        table_block.style.display = 'block'
      } else {
        let table_block = document.querySelector('#table_block')
        table_block.style.display = 'none'
      }
    }
  }

  let table_responsive = document.querySelector('.table-responsive')
  let tab_block = document.querySelector('.tab_block')

  if(table_responsive) {
    table_responsive.addEventListener('scroll', horiz_scroll)
  }
  if(tab_block) {
    tab_block.addEventListener('scroll', horiz_scroll_head)
  }
  function horiz_scroll (e) {
    if(wi < 845) {
      tab_block.scrollLeft = e.target.scrollLeft
    }
  }

  function horiz_scroll_head (e) {
    if(wi < 845) {
      let table_block_origin = document.querySelector('.table_block_origin')
      table_block_origin.scrollLeft = e.target.scrollLeft
    }
  }
})
